'use strict';

var objectKeys = require( '@stdlib/utils/keys' );
var ns = require( './../lib' );

// Example showcasing the use of a function from the math/iter/special namespace
var iterSqrt = ns.iterSqrt;
var array = [1, 4, 9, 16];
var it = iterSqrt( array );

var v;
while ( ( v = it.next().value ) ) {
    console.log( v );
}
// => 1
// => 2
// => 3
// => 4
